def isBeautiful(m, p):
    l = p.index(m)
    r = p.index(m, l + 1)
    if l == r:
        return False
    if isPermutation(p[l:r + 1]):
        return True
    return False

def isPermutation(p):
    for i in range(len(p)):
        if p[i] != i + 1:
            return False
    return True

