n, p, k = map(int, input().split())
a = list(map(int, input().split()))
a.sort()
count = 0
for i in range(n):
    for j in range(i+1, n):
        if (a[i] + a[j]) * (a[i]**2 + a[j]**2) % p == k:
            count += 1
print(count)

